function TestePublicController($scope, Teste, share, $log) 
{	
	$scope.share = share;
	$scope.createOrUpdate = function() 
	{
		if($scope.teste._id != null)
			teste.update($scope.teste);
		else
			teste.save($scope.teste);
		$scope.reset();
		$scope.query();
	};
	$scope.destroy = function(index)
	{
		//var id = $scope.items[index]._id;
		$scope.items[index].$delete();
		$scope.items.splice(index, 1);
		$scope.query();
	};
	$scope.query = function()
	{
		$scope.items = teste.query();		
	};
	$scope.select = function(index)
	{
		$scope.teste = $scope.items[index];		
	};
	$scope.reset = function()
	{
		delete $scope.teste;		
	};
}
